mapper.insert:
insert into receivetask (id, receiveaccount, finishtime
  )
values (#{id,jdbcType=INTEGER}, #{receiveaccount,jdbcType=INTEGER}, #{finishtime,jdbcType=TIMESTAMP}
  )

mapper.update:
update receivetask
set receiveaccount = #{receiveaccount,jdbcType=INTEGER},
  finishtime = #{finishtime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, receiveaccount, finishtime
from receivetask
where id = #{id,jdbcType=INTEGER}
