mapper.update:
update receivetask
set receiveaccount = #{receiveaccount,jdbcType=INTEGER},
  finishtime = #{finishtime,jdbcType=TIMESTAMP},
  iid = #{iid,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, receiveaccount, finishtime, iid
from receivetask
where id = #{id,jdbcType=INTEGER}
